CREATE TABLE BRAND (BRAND_ID int NOT NULL, BRAND_NAME VARCHAR(64) NOT NULL, PRIMARY KEY(BRAND_ID));
CREATE TABLE PRODUCT(PRODUCT_ID int NOT NULL, NAME VARCHAR(64) NOT NULL, PRIMARY KEY(PRODUCT_ID));
CREATE TABLE PRICES (ID int NOT NULL, PRODUCT_ID int NOT NULL, BRAND_ID int NOT NULL, START_DATE DATETIME NOT NULL, END_DATE DATETIME NOT NULL, PRICE_LIST int NOT NULL, PRIORITY int NOT NULL, PRICE DOUBLE NOT NULL, CURR VARCHAR(25) NOT NULL, PRIMARY KEY(ID), FOREIGN KEY(PRODUCT_ID) REFERENCES PRODUCT(PRODUCT_ID), FOREIGN KEY(BRAND_ID) REFERENCES BRAND(BRAND_ID));